const (
	yamlGold  = "testdata/secret.yaml"
	jsonGold  = "testdata/secret.json"
	bytesGold = "testdata/secret.bytes"
	badSecret = "testdata/badSecret"
)

func TestDecode(t *testing.T) {
	file, err := os.ReadFile(yamlGold)
	if err != nil {
		t.Error(err)
		return
	}

	s, err := decode(file)
	if err != nil {
		t.Error(err)
		return
	}

	expected, err := os.ReadFile(bytesGold)
	if err != nil {
		t.Error(err)
		return
	}

	assert.Equal(t, string(expected), s.String())
}

func TestDecodeBadSecret(t *testing.T) {
	file, err := os.ReadFile(badSecret)
	if err != nil {
		t.Error(err)
		return
	}

	_, err = decode(file)
	assert.Error(t, err)
}

func captureConsoleOutput(f func()) []byte {
	var buf bytes.Buffer
	pterm.SetDefaultOutput(&buf)

	f()

	pterm.SetDefaultOutput(os.Stderr)
	return buf.Bytes()
}

